def get_combat_tier(rating: float) -> str:
    """Get combat tier based on rating"""
    if rating < 100:
        return "Novice"
    elif rating < 200:
        return "Apprentice"
    elif rating < 350:
        return "Warrior"
    elif rating < 500:
        return "Veteran"
    elif rating < 700:
        return "Elite"
    elif rating < 1000:
        return "Master"
    elif rating < 1500:
        return "Legend"
    else:
        return "Mythic"


