# Helper: human-friendly currency
def format_currency(amount: int) -> str:
    if amount < 0:
        return f"âš ï¸ **DEBT** {MONEY_ICON} {abs(amount):,}"
    else:
        return f"{MONEY_ICON} {amount:,}"


